update.foreach:
UPDATE t_info_goods SET goods_inventory = #{item.goodsInventory},
goods_state_id = 0
update_time = NOW(), update_user = #{item.updateUser}, version = version + 1 WHERE goods_id = #{item.goodsId}

mapper.select:
SELECT a.order_id orderId, a.order_state_id orderStateId, a.order_all_cost orderAllCost, a.order_all_goods_count orderAllGoodsCount, a.version FROM t_info_order a WHERE a.is_delete = 0 AND a.user_id = #{userId}
AND a.order_state_id = #{orderStateId}
AND a.order_state_id IN (4,5)

SELECT a.order_id orderId, c.goods_name goodsName, c.goods_image_path goodsImagePath, c.goods_describe goodsDescribe, c.goods_price goodsPrice, b.goods_count cartGoodsCount, b.goods_id goodsId FROM t_info_order a LEFT JOIN t_info_order_deepen b ON b.order_id = a.order_id LEFT JOIN t_info_goods c ON c.goods_id = b.goods_id WHERE a.is_delete = 0 AND a.order_id IN
#{item}
AND a.order_state_id = #{orderStateId}
AND a.order_state_id IN (4,5)
