mapper.select:
SELECT

    IF( (SELECT COUNT(invite_code) FROM t_info_store WHERE invite_code = #{inviteCode})=0,4,0) +

    IF(count(user_acct) != 0,1,0 ) +
    IF( (select count(phone) from t_sys_user where is_delete = 0
    and phone = #{phone}

        and user_id != #{userId}


        and role = #{role}
    )!=0,2,0)
FROM
    t_sys_user
WHERE
    is_delete = 0
    AND user_acct = #{userAcct}

    AND user_id != #{userId}
