mapper.select:
SELECT
    IF( COUNT(business_code)!= 0,1,0 ) +
    IF( (SELECT COUNT(user_id) FROM t_sys_user WHERE is_delete = 0 AND user_id = #{userId})=0,2,0 ) +
    IF( (SELECT COUNT(phone) FROM t_info_store WHERE is_delete = 0 AND phone = #{phone}

            AND store_id != #{storeId}
        ) != 0,4,0 ) +
    IF( (SELECT COUNT(user_id) FROM t_info_store WHERE is_delete = 0 AND user_id = #{userId}

            AND store_id != #{storeId}
        ) != 0,8,0 )
FROM
    t_info_store
WHERE
    is_delete = 0
    AND business_code = #{businessCode}

    AND store_id != #{storeId}

SELECT a.store_id storeId, a.store_name storeName, a.phone, a.address, b.user_name userName, a.invite_code inviteCode, b.user_acct userAcct, a.version FROM t_info_store a LEFT JOIN t_sys_user b ON a.user_id = b.user_id WHERE a.is_delete = 0
AND a.store_id LIKE concat('%',#{store.storeId},'%')
AND a.store_name LIKE concat('%',#{store.storeName},'%')
AND b.user_name LIKE concat('%',#{userName},'%')
AND a.province_id = #{store.provinceId}
AND a.city_id = #{store.cityId}
AND a.area_id = #{store.areaId}
AND a.user_id = #{store.userId}
ORDER BY a.create_time DESC
