mapper.select:
SELECT a.driver_id driverId, b.user_name userName, b.phone, b.id_card idCard, b.user_acct userAcct, a.version FROM t_info_driver a LEFT JOIN t_sys_user b ON a.driver_id = b.user_id WHERE a.is_delete = 0
AND a.driver_id LIKE concat('%',#{Dr.driverId},'%')
AND b.user_name LIKE concat('%',#{Dr.userName},'%')
AND a.province_id = #{Dr.provinceId}
AND a.city_id = #{Dr.cityId}
AND a.area_id = #{Dr.areaId}
AND b.user_id = #{nowLogin};
ORDER BY a.create_time DESC

mapper.update:
UPDATE t_info_driver SET is_delete = 1, update_time =NOW(), update_user = #{updateUser} WHERE driver_id IN
#{item}
UPDATE t_sys_user SET is_delete = 1, update_time = NOW(), update_user = #{updateUser} WHERE user_id IN
#{item}
