update.foreach:
UPDATE t_info_goods
goods_inventory = #{item.goodsInventory}, update_time = NOW(), update_user = #{item.updateUser}, version = version + 1
goods_id = #{item.goodsId,jdbcType=VARCHAR} AND version = #{item.version}

UPDATE t_info_goods
goods_sales = goods_sales - #{item.goodsCount}, update_time = NOW(), update_user = #{item.updateUser}, version = version + 1
goods_id = #{item.goodsId,jdbcType=VARCHAR} AND version = #{item.version}

UPDATE t_info_order
order_state_id = #{item.orderStateId,jdbcType=VARCHAR}, update_time = NOW(), update_user = #{item.updateUser,jdbcType=VARCHAR}, version = version + 1
order_id = #{item.orderId} AND version = #{item.version,jdbcType=VARCHAR}
