mapper.select:
SELECT
    COUNT(phone)
FROM
    t_sys_user
WHERE
    is_delete = 0
    AND phone = #{phone}

    AND user_id != #{userId}


    AND role = #{role}

SELECT
IF(COUNT(user_acct) != 0,1,0 ) +
IF( (SELECT COUNT(phone) FROM t_sys_user WHERE is_delete = 0
        AND phone = #{phone}

        AND user_id != #{userId}


        AND role = #{role}
    )!=0,2,0)
FROM
    t_sys_user
WHERE
    is_delete = 0
    AND user_acct = #{userAcct}

    AND user_id != #{userId}
