mapper.select:
SELECT a.order_id orderId, a.order_all_cost orderAllCost, a.order_state_id orderStateId, a.store_id storeId, a.user_name userName, a.phone phone, a.version version, a.pay_time payTime FROM t_info_order a WHERE a.is_delete = 0
AND a.user_id = #{nowLogin}
AND a.user_name LIKE concat('%', #{o.userName}, '%')
AND a.phone LIKE concat('%', #{o.phone}, '%')
AND a.order_id LIKE concat('%', #{o.orderId}, '%')
AND a.order_state_id LIKE concat('%', #{o.orderStateId}, '%')
AND a.pay_time >= #{payTimeStart}
AND a.pay_time <= #{payTimeEnd}
ORDER BY create_time DESC

update.foreach:
UPDATE t_info_order
order_state_id = #{item.orderStateId,jdbcType=VARCHAR}, update_time = NOW(), update_user = #{item.updateUser,jdbcType=VARCHAR}, version = version + 1
order_id = #{item.orderId} AND version = #{item.version,jdbcType=VARCHAR}
